import React from "react";

const WeatherDisplay = ({ weatherData }) => {
  if (!weatherData) {
    return null;
  }

  const { city, current, daily, alerts } = weatherData;

  return (
    <div>
      <h2>Weather Information for {city}</h2>
      <h3>Current Weather</h3>
      <p>Temperature: {current.temp} K</p>
      <p>Feels Like: {current.feels_like} K</p>
      <p>Pressure: {current.pressure} hPa</p>
      {/* Display other relevant data from 'current' section */}

      <h3>Daily Forecast</h3>
      {daily.map((day, index) => (
        <div key={index}>
          <p>Date: {new Date(day.dt * 1000).toLocaleDateString()}</p>
          <p>Day Temperature: {day.temp_day} K</p>
          <p>Night Temperature: {day.temp_night} K</p>
          <p>Weather: {day.weather}</p>
          {/* Display other relevant data from 'daily' section */}
        </div>
      ))}

      <h3>Alerts</h3>
      {alerts.map((alert, index) => (
        <div key={index}>
          <p>Event: {alert.event}</p>
          <p>Description: {alert.description}</p>
          {/* Display other relevant data from 'alerts' section */}
        </div>
      ))}
    </div>
  );
};

export default WeatherDisplay;
